mapper.insert:
insert
	  into coupon
	  (
	  	cp_no
	  , cp_name
	  , cp_price
	  , cp_lower
	  , cp_limit
	  , cp_condition
	  )
	  values
	  (
	    seq_cpco.nextval
	  , #{cpName}
	  , #{cpPrice}
	  , #{cpLower}
	  , #{cpLimit}
	  , #{cpCondition}
	  )

mapper.delete:
delete
from coupon
where cp_no = #{ cpNo }
