mapper.insert:
insert
  into member
  (
  	mem_no
  , mem_id
  , mem_pwd
  , mem_name
  , phone
  , email

  )
  values
  (
    seq_mno.nextval
  , #{memId}
  , #{memPwd}
  , #{memName}
  , #{phone}
  , #{email}

  )

  	insert
	  	  into address_list
	  	  (
	  	  	ad_no
	  	  , mem_no
	  	  , ad_name
	  	  , ad_phone
	  	  , ad_post
	  	  , ad_road
	  	  , ad_detail

	  	  )
	  	  values
	  	  (
	  	    seq_adno.nextval
	  	  , #{memNo}
	  	  , #{adName}
	  	  , #{adPhone}
	  	  , #{adPost}
	  	  , #{adRoad}
	  	  , #{adDetail}
	  	  )
