mapper.insert:
insert
  into creator
  (
    cre_no
  , mem_no
  , cre_business
  , cre_privacy
  , cre_name
  )
  values
  (
    seq_ctco.nextval
  , #{memNo}
  , #{creBusiness}
  , #{crePrivacy}
  , #{creName}
  )

insert
  into project
  (
    pro_no
  , cre_no
  , ran_no
  )
  values
  (
    seq_pjco.nextval
  , #{creNo}
  , #{ranNo}
  )

mapper.update:
update
       pro_require
   set
	   reward_plan = #{rewardPlan}
	 , reward_delivery = #{rewardDelivery}
	 , reward_category = #{rewardCategory}
 where pro_no = #{proNo}

mapper.select:
select count(*)
  from tb_like
 where mem_no = #{memNo}
   and pro_no = #{proNo}

select count(*)
  from tb_like
 where pro_no = #{proNo}

mapper.delete:
delete from tb_like
where mem_no = #{memNo}
  and pro_no = #{proNo}

mapper.insert:
insert
  into tb_like
  (
    mem_no
  , pro_no
  )
  values
  (
  	#{memNo}
  , #{proNo}
  )
